template.value:
SharedPreferences sharedpreferences = getSharedPreferences("MyPrefrence", MODE_PRIVATE); //where MyPREFERENCES may be any name
SharedPreferences.Editor editor = sharedpreferences.edit();
editor.putString("", "");  //here we can use many fields
editor.commit();

Intent emailIntent = new Intent(android.content.Intent.ACTION_SEND);
String aEmailList[] = {"user@host.com"};
String aEmailCCList[] = { "user2@host.com"};
String aEmailBCCList[] = { "user3@host.com" };
emailIntent.putExtra(android.content.Intent.EXTRA_EMAIL, aEmailList);

emailIntent.putExtra(android.content.Intent.EXTRA_CC, aEmailCCList);
emailIntent.putExtra(android.content.Intent.EXTRA_BCC, aEmailBCCList);
emailIntent.putExtra(android.content.Intent.EXTRA_SUBJECT, " Sample Subject");
emailIntent.setType("plain/text");
emailIntent.putExtra(android.content.Intent.EXTRA_TEXT, "Sample Message Body");

startActivity(emailIntent);
